// Component for a search form
// which search 3 days weather forecast by a location entered in the text box

import React, { useState } from "react";

export function SearchForecast(props) {
  const APIID = "431416ee6ac11ff008eca9e762cf30d3"; // Access key for OpenWeather
  const [location, setLocation] = useState(props.city);

  function handleSubmit(e) {
    e.preventDefault();
    console.log("")
    getCityData(location);
  }

  // get lat and lon from city name
  async function getCityData(city_name) {
    try {
      const response = await fetch(
        `http://api.openweathermap.org/geo/1.0/direct?q=${city_name}&limit=1&appid=${APIID}`
      );

      // No error in response data
      if (response.ok) {
        // empty object
        if (Object.hasOwn(response)) {
          console.log("No data: ", Object.keys(response).length);
          throw new Error(Object.hasOwn(response));
        } else {
          const city_data = await response.json();

          return city_data;
        }
      } else {
        throw new Error(response.status);
      }
    } catch (err) {
      console.error("ERROR", err);
      return err;
    }
  }

  return (
    <form onSubmit={handleSubmit}>
      <div className="search_form">SearchForecast</div>
      <label htmlFor="search_box">Location</label>
      <input
        id="search_box"
        type="text"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />
      <button type="submit" className="search_btn">
        Go
      </button>
    </form>
  );
}
