import React from "react";
import { Forecast } from "./Forecast";

export function ThreeDaysForecast({ city_lat, city_lon }) {
  //get weather data from its position(lat, lon)
  async function getWeatherData(lat, lon, ) {
    console.log(lat, lon);
    try {
      const response = await fetch(
        `http://api.openweathermap.org/data/2.5/forecast/daily?lat=${lat}&lon=${lon}&appid=${APIID}`
      );
      // No error in response data
      if (response.ok) {
        const weather_data = await response.json();
        console.log(weather_data);
        return weather_data;
      } else {
        throw new Error(response.status);
      }
    } catch (err) {
      console.error("ERROR", err);
      return err;
    }
  }

  return (
    <div>
      <h2>ThreeDaysForecast</h2>
      <div>{getWeatherData(city_lat, city_lon)}</div>
      <Forecast />
      <Forecast />
      <Forecast />
    </div>
  );
}
