import "./styles/styles.css";
import { useState } from "react";
import Location from "./components/Location";

const APIID = "431416ee6ac11ff008eca9e762cf30d3";
const DEFAULT_CITY = "Calgary"; //later it will be determine from user's location

function App() {
  const [cityName, setCityName] = useState(DEFAULT_CITY);
  const [data, setData] = useState({
    date: "2023-05-12",
    weather_id: "400",
    main_weather: "Clear",
    highest_tmp: "100",
    lowest_tmp: "20",
  });
  async function renderWeather() {
    getCityData(cityName)
      .then((city) => {
        return getWeatherData(city[0].lat, city[0].lon);
      })
      .then((weather_data) => {
        setData({
          date: getMonDay(new Date(weather_data.list[0].dt * 1000)),
          weather_id: weather_data.list[0].weather[0].id,
          main_weather: weather_data.list[0].weather[0].main,
          highest_tmp: parseInt(weather_data.list[0].temp.max - 273.15) + "邃ソ

No data: ", Object.keys(response).length);
          throw new Error(Object.hasOwn(response));
        } else {
          const city_data = await response.json();

          return city_data;
        }
      } else {
        throw new Error(response.status);
      }
    } catch (err) {
      console.error("ERROR", err);
      return err;
    }
  }

  //get weather data from its position(lat, lon)
  async function getWeatherData(lat, lon) {
    console.log(lat, lon);
    try {
      const response = await fetch(
        `http://api.openweathermap.org/data/2.5/forecast/daily?lat=${lat}&lon=${lon}&appid=${APIID}`
      );
      // No error in response data
      if (response.ok) {
        const weather_data = await response.json();
        return weather_data;
      } else {
        throw new Error(response.status);
      }
    } catch (err) {
      console.error("ERROR", err);
      return err;
    }
  }

  function getMonDay(date) {
    let month_str = "";
    switch (date.getMonth()) {
      case 0:
        month_str = "Jan";
        break;
      case 1:
        month_str = "Feb";
        break;
      case 2:
        month_str = "Mar";
        break;
      case 3:
        month_str = "Apr";
        break;
      case 4:
        month_str = "May";
        break;
      case 5:
        month_str = "Jan";
        break;
      case 6:
        month_str = "Jul";
        break;
      case 7:
        month_str = "Aug";
        break;
      case 8:
        month_str = "Sep";
        break;
      case 9:
        month_str = "Oct";
        break;
      case 10:
        month_str = "Nov";
        break;
      case 11:
        month_str = "Dec";
        break;
    }

    let date_str = month_str + " " + date.getDate();
    return date_str;
  }

  return (
    <>
      <Location />
      <header>
        <h1 className="title">Weather Forecast</h1>
      </header>
      <div className="container">
        <div className="location">
          <form>
            <p>
              <label htmlFor="city">Location</label>
              <input
                id="city"
                name="city"
                type="text"
                value={cityName}
                onChange={(e) => {
                  setCityName(e.target.value);
                }}
                // onKeyUp={}
              />
            </p>
            <p className="search">
              <input
                type="button"
                value="Go"
                id="search_btn"
                onClick={renderWeather}
              />
            </p>
          </form>

          <div className="forecasts">
            <section className="day_1">
              <div className="date" id="date_1">
                {data.date}
              </div>
              <div className="weather">
                <p>
                  <img id="weather_pic_1" src="" alt="" />
                </p>
                <p id="weather_1">{data.main_weather}</p>
              </div>
              <div className="highest_temp">
                <label for="highest_1">Highest</label>
                <p id="highest_1">{data.highest_tmp}</p>
              </div>
              <div className="lowest_temp">
                <label for="lowest_1">Lowest</label>
                <p className="lowest_tmp" id="lowest_1">
                  {data.lowest_tmp}
                </p>
              </div>
            </section>
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
