import React, { useState } from "react";

export function SearchForecast(props) {
  const [location, setLocation] = useState(props.city);

  return (
    <form>
      <div className="search_form">SearchForecast</div>
      <label htmlFor="search_box">Location</label>
      <input
        id="search_box"
        type="text"
        value={location}
        onChange={(e) => setLocation(e.target.value)} //change value
      />
      <button className="search_btn">Go</button>
    </form>
  );
}
