import "./styles/styles.css";
import { useState } from "react";
import Location from "./components/Location";

const APIID = "431416ee6ac11ff008eca9e762cf30d3";
const DEFAULT_CITY = "Calgary"; //later it will be determine from user's location

function App() {

  return (
    <>
      <Location />
      <header>
        <h1 className="title">Weather Forecast</h1>
      </header>
      <div className="container">
        <div className="location">
          <form>
            <p>
              <label htmlFor="city">Location</label>
              <input
                id="city"
                name="city"
                type="text"
                value={cityName}
                onChange={(e) => {
                  setCityName(e.target.value);
                }}
                // onKeyUp={}
              />
            </p>
            <p className="search">
              <input
                type="button"
                value="Go"
                id="search_btn"
                onClick={renderWeather}
              />
            </p>
          </form>

          <div className="forecasts">
            <section className="day_1">
              <div className="date" id="date_1">
                {data.date}
              </div>
              <div className="weather">
                <p>
                  <img id="weather_pic_1" src="" alt="" />
                </p>
                <p id="weather_1">{data.main_weather}</p>
              </div>
              <div className="highest_temp">
                <label for="highest_1">Highest</label>
                <p id="highest_1">{data.highest_tmp}</p>
              </div>
              <div className="lowest_temp">
                <label for="lowest_1">Lowest</label>
                <p className="lowest_tmp" id="lowest_1">
                  {data.lowest_tmp}
                </p>
              </div>
            </section>
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
