import React, { useState } from "react";

export default class SearchForecast extends React.Component {
  render() {
    const [location, setLocation] = useState(this.props.city);

    return (
      <form>
        <div className="search_form">SearchForecast</div>
        <label htmlFor="search_box">Location</label>
        <input
          id="search_box"
          type="text"
          value={this.props.city}
          onChange={(e) => setLocation(e.target.value)}
        />
        <button className="search_btn">Go</button>
      </form>
    );
  }
}
